import drawsvg as draw
import numpy as np

size = 5
    
def gernerate(Binary):
    d = draw.Drawing(256, 256, origin='center')
    counter = 0;
    for i in range(1,8):
        r = int(100 / 8 * i)  #   100 / 8 * i * 1.025
        n = int(100 / 8 * i* np.pi * 2 / size)
        step = 360 / n
        d.append(draw.Circle(10, 10, r,fill='none',stroke='black', stroke_width=0.5))
        for u in range(n):
            t = np.radians(u * step)
            x = (r * np.cos(t) + 10)
            y = (r * np.sin(t) + 10)
            if len(Binary) > i + u:
                if Binary[i + u] == '1':
                    d.append(draw.Circle(x, y, 2,fill='black'))
            counter += 1
            #d.append(draw.Text(str(counter), 3, x, y, fill='red'))

    d.append(draw.Circle(10, 10, 100,fill='none',stroke='black', stroke_width=2))

    d.save_svg('example.svg')

